#include <stdio.h>
#include <stdlib.h>
#include <errno.h>
#include <err.h>

/*
 * Checks if strtod() reports underflow.
 */

int
main()
{
	char *tmp="0.";
	double d;

	d = strtod(tmp, NULL);
	if (errno != ERANGE)
		errx(1, "errno = %d", errno);
	return (0);
}
